mapper.select:
SELECT ITEM_CLS AS itemCls, ITEM_CLS_NM AS itemClsNm, USE_FLAG AS useFlag, ITEM_LVL AS itemLvl, PARENT_ITEM_CLS AS parentItemCls FROM MTCA
AND PARENT_ITEM_CLS IS NULL
AND PARENT_ITEM_CLS = #{parentItemCls}

SELECT
    COUNT(*)
FROM
    MTCA
WHERE

    (
        ITEM_CLS_NM = #{item.itemClsNm}
    AND
        PARENT_ITEM_CLS = #{item.parentItemCls} 
    AND
        ITEM_CLS = #{item.itemCls}
    AND
        ITEM_LVL = #{item.itemLvl}
    AND
        DEL_FLAG = 'N'
    )

mapper.insert:
INSERT INTO MTCA( ITEM_CLS, PARENT_ITEM_CLS, ITEM_LVL, ITEM_CLS_NM, REG_DATE, REG_USER_ID, MOD_DATE, MOD_USER_ID, DEL_FLAG, USE_FLAG ) VALUES
( #{item.itemCls}, #{item.parentItemCls}, #{item.itemLvl}, #{item.itemClsNm}, #{item.createdAt}, #{item.createdBy}, #{item.modifiedAt}, #{item.modifiedBy}, 'N', #{item.useFlag} )
/* [핵심] 키가 중복되면(이미 삭제된 데이터가 있으면) 아래처럼 업데이트해라 */ ON DUPLICATE KEY UPDATE ITEM_CLS_NM = VALUES(ITEM_CLS_NM), -- 이름은 새로 입력한 걸로 변경 USE_FLAG = VALUES(USE_FLAG), -- 사용 여부도 새 걸로 변경 DEL_FLAG = 'N', -- [중요] 삭제된 걸 다시 살려냄 (N으로 변경) PARENT_ITEM_CLS = VALUES(PARENT_ITEM_CLS), -- 부모 카테고리도 변경될 수 있으니 업데이트 MOD_DATE = NOW() -- 수정일 업데이트
