mapper.select:
SELECT ph.PR_NUM as prNum, ph.PR_SUBJECT as prSubject, DATE(ph.REG_DATE) as prDate, u.USER_NM as requester, d.DEPT_NM as reqDeptNm, ph.PROGRESS_CD as progressCd, c1.CODE_NAME as progressNm, ph.PC_TYPE as pcType, c2.CODE_NAME as pcTypeNm, COUNT(pd.ITEM_CD) as itemCount, ph.PR_AMT as totalAmount FROM PRHD ph LEFT JOIN PRDT pd ON pd.PR_NUM = ph.PR_NUM AND pd.DEL_FLAG = 'N' LEFT JOIN BY_USER u ON u.USER_ID = ph.REG_USER_ID LEFT JOIN DEPT d ON d.DEPT_CD = ph.DEPT_CD LEFT JOIN CODD c1 ON c1.CODE_GROUP = 'PROGRESS_CD' AND c1.CODE = ph.PROGRESS_CD LEFT JOIN CODD c2 ON c2.CODE_GROUP = 'PC_TYPE' AND c2.CODE = ph.PC_TYPE WHERE ph.DEL_FLAG = 'N' AND ph.PROGRESS_CD = 'A' AND ph.PC_TYPE NOT IN ('C', 'E') AND NOT EXISTS ( SELECT 1 FROM RFQHD r WHERE r.PR_NUM = ph.PR_NUM AND r.DEL_FLAG = 'N' )
AND ph.PR_NUM LIKE CONCAT('%', #{prNum}, '%')
AND ph.PR_SUBJECT LIKE CONCAT('%', #{prSubject}, '%')
AND DATE(ph.REG_DATE) >= #{fromDate}
AND DATE(ph.REG_DATE) <= #{toDate}
AND ph.DEPT_CD = #{reqDeptCd}
AND u.USER_NM LIKE CONCAT('%', #{reqUserNm}, '%')
GROUP BY ph.PR_NUM, ph.PR_SUBJECT, DATE(ph.REG_DATE), u.USER_NM, d.DEPT_NM, ph.PROGRESS_CD, c1.CODE_NAME, ph.PC_TYPE, c2.CODE_NAME, ph.PR_AMT ORDER BY MAX(ph.REG_DATE) DESC
